tables.table_schema:
CREATE TABLE QuantumSensors (
  sensor_id INT PRIMARY KEY,
  sensor_name VARCHAR(255),
  calibration_date DATE
);

CREATE TABLE sensor_calibration_logs (
    sensor_id INT PRIMARY KEY,
    calibration_date DATE,
    calibration_result FLOAT
);

CREATE TABLE sensor_metadata (
    sensor_id INT PRIMARY KEY,
    sensor_type VARCHAR(50),
    location VARCHAR(100),
    calibration_frequency INT
);

CREATE TABLE calibration_technicians (
    technician_id INT PRIMARY KEY,
    technician_name VARCHAR(50),
    technician_email VARCHAR(100)
);

CREATE TABLE sensors (
    sensor_id SERIAL PRIMARY KEY,
    serial_number VARCHAR(50) UNIQUE NOT NULL,
    manufacturer VARCHAR(50) NOT NULL,
    model VARCHAR(50) NOT NULL
);

CREATE TABLE technicians (
    technician_id SERIAL PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE
);

CREATE TABLE calibration_procedures (
    procedure_id SERIAL PRIMARY KEY,
    procedure_name VARCHAR(100) UNIQUE NOT NULL,
    description TEXT
);
