tables.table_schema:
CREATE TABLE anomalies (
  sensor_id INT PRIMARY KEY,
  anomaly_code VARCHAR(255),
  anomaly_description TEXT,
  anomaly_timestamp TIMESTAMP
);

CREATE TABLE error_codes (
  error_code VARCHAR(10) PRIMARY KEY,
  error_description TEXT,
  error_severity INT
);

CREATE TABLE sensor_anomalies (
    id SERIAL PRIMARY KEY,
    sensor_id INT,
    anomaly_description TEXT,
    anomaly_timestamp TIMESTAMP
);

CREATE TABLE error_codes (
    code_id SERIAL PRIMARY KEY,
    error_code INT,
    error_description TEXT
);

CREATE TABLE sensor_network (
    network_id SERIAL PRIMARY KEY,
    network_name TEXT,
    network_location TEXT
);

CREATE TABLE SensorNetwork (
  sensor_id INT PRIMARY KEY,
  sensor_name VARCHAR(50) NOT NULL,
  location VARCHAR(100),
  status ENUM('active', 'inactive')
);

CREATE TABLE ErrorCodes (
  error_code INT PRIMARY KEY,
  error_description VARCHAR(100) NOT NULL
);

CREATE TABLE anomaly_logs (
    id SERIAL PRIMARY KEY,
    sensor_id INT,
    anomaly_type VARCHAR(50),
    anomaly_timestamp TIMESTAMP,
);

CREATE TABLE error_codes (
    code_id SERIAL PRIMARY KEY,
    error_code INT,
    error_description TEXT,
);

CREATE TABLE sensor_networks (
    network_id SERIAL PRIMARY KEY,
    network_name VARCHAR(50),
    network_location VARCHAR(100),
);

CREATE TABLE sensor_readings (
    reading_id SERIAL PRIMARY KEY,
    sensor_id INT,
    reading_value FLOAT,
    reading_time TIMESTAMP,
);

CREATE TABLE sensor_status (
    status_id SERIAL PRIMARY KEY,
    sensor_id INT,
    status VARCHAR(50),
    status_time TIMESTAMP,
);
