tables.table_schema:
CREATE TABLE lighting_preferences (
    user_id INT PRIMARY KEY,
    schedule_id INT,
    brightness INT,
    color_temperature INT
);

CREATE TABLE users_preferences (
    user_id UUID PRIMARY KEY,
    lighting_schedule JSON,
    lighting_settings JSON
);

CREATE TABLE users_preferences (
   user_id INT PRIMARY KEY,
   lighting_schedule VARCHAR(50),
   lighting_settings VARCHAR(50)
);

CREATE TABLE lighting_schedules (
   schedule_id INT PRIMARY KEY,
   schedule_name VARCHAR(50),
   start_time TIME,
   end_time TIME
);

CREATE TABLE automation_routines (
   routine_id INT PRIMARY KEY,
   routine_name VARCHAR(50),
   trigger_event VARCHAR(50),
   action VARCHAR(50)
);

CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    lighting_schedule VARCHAR(255),
    lighting_intensity INT,
    lighting_color VARCHAR(50)
);

CREATE TABLE LightingSettings (
    setting_id INT PRIMARY KEY,
    schedule_id INT,
    brightness_level INT,
    color_temperature INT
);

CREATE TABLE AutomationRoutines (
    routine_id INT PRIMARY KEY,
    routine_name VARCHAR(50),
    start_time TIME,
    end_time TIME
);

CREATE TABLE UserSettings (
    setting_id INT PRIMARY KEY,
    user_id INT,
    schedule_id INT,
    preferred_brightness INT
);

CREATE TABLE ScheduleDetails (
    schedule_id INT PRIMARY KEY,
    schedule_name VARCHAR(50),
    day_of_week VARCHAR(20),
    is_active BOOLEAN
);
