tables.table_schema:
CREATE TABLE IoTDevices (
    device_id UUID PRIMARY KEY,
    device_name STRING,
    device_type STRING,
    device_location STRING
);

CREATE TABLE ExternalSystems (
    system_id UUID PRIMARY KEY,
    system_name STRING,
    system_type STRING,
    system_location STRING
);

CREATE TABLE InteroperabilityMappings ( 
    mapping_id INT PRIMARY KEY, 
    device_id INT, 
    system_id INT, 
    mapping_details JSONB 
);

CREATE TABLE Locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE ExternalSystems (
    system_id INT PRIMARY KEY,
    system_name VARCHAR(255),
    system_type VARCHAR(255),
    endpoint_url VARCHAR(255)
);
