tables.table_schema:
CREATE TABLE EnergyConsumption (
    id UUID PRIMARY KEY,
    date DATE,
    time TIME,
    energy_consumed FLOAT
);

CREATE TABLE EnergyUsageTrends (
    id UUID PRIMARY KEY,
    date DATE,
    energy_saved FLOAT,
    trend_analysis TEXT
);

CREATE TABLE EnergyConsumptionData (
  timestamp TIMESTAMP,
  device_id INT,
  energy_usage FLOAT
);

CREATE TABLE Devices (
  device_id INT,
  device_name VARCHAR(50),
  location VARCHAR(50)
);

CREATE TABLE EnergyUsageTrends (
  trend_id INT,
  timestamp DATE,
  trend_type VARCHAR(20),
  trend_description TEXT
);

CREATE TABLE energy_consumption_data (
    timestamp TIMESTAMP,
    device_id INT,
    energy_consumed FLOAT
);

CREATE TABLE devices (
    device_id INT,
    device_name VARCHAR,
    device_type VARCHAR,
    location VARCHAR
);

CREATE TABLE homes (
    home_id INT,
    address VARCHAR,
    owner_name VARCHAR
);

CREATE TABLE energy_usage_analysis (
    analysis_id INT,
    timestamp TIMESTAMP,
    analysis_result VARCHAR
);

CREATE TABLE energy_consumption_history (
    timestamp TIMESTAMP,
    usage_kwh DECIMAL,
    appliance_id INT,
    room_id INT
);

CREATE TABLE appliances (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(255),
    category VARCHAR(50)
);

CREATE TABLE rooms (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(255)
);

CREATE TABLE energy_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50)
);

CREATE TABLE device_status (
    appliance_id INT,
    timestamp TIMESTAMP,
    status BOOLEAN
);
