tables.table_schema:
CREATE TABLE ImageRecognitionData (
    id INT PRIMARY KEY,
    image_url TEXT,
    facial_features JSONB,
    confidence_level FLOAT
);

CREATE TABLE facial_recognition_data (
    id INT PRIMARY KEY,
    image_path VARCHAR(255),
    recognition_result JSON
);

CREATE TABLE facial_recognition_logs (
    log_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    user_id INT,
    action VARCHAR(50)
);

CREATE TABLE image_data (
    image_id UUID PRIMARY KEY,
    image_path VARCHAR(255) NOT NULL,
    image_data BYTEA NOT NULL
);

CREATE TABLE facial_features (
    face_id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    feature_data JSONB NOT NULL,
    CONSTRAINT fk_image FOREIGN KEY (image_id) REFERENCES image_data(image_id)
);

CREATE TABLE FaceRecognition (
    face_id INT PRIMARY KEY,
    face_image_id INT,
    recognition_result VARCHAR(255),
    confidence_level FLOAT
);

CREATE TABLE AIAlgorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(255),
    algorithm_description TEXT
);
