tables.table_schema:
CREATE TABLE AirQualitySensorData (
  sensor_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  temperature FLOAT,
  humidity FLOAT
);

CREATE TABLE AirPurifierSettings (
  setting_id INT PRIMARY KEY,
  sensor_id INT,
  target_temperature FLOAT,
  target_humidity FLOAT
);

CREATE TABLE AirPurifierHistory (
  history_id INT PRIMARY KEY,
  sensor_id INT,
  time_started TIMESTAMP,
  time_ended TIMESTAMP
);

CREATE TABLE RoomSensorLocation (
    room_id INT PRIMARY KEY,
    sensor_id INT,
    room_name VARCHAR(50),
    location_details VARCHAR(100)
);

CREATE TABLE SensorData (
  sensor_id UUID PRIMARY KEY,
  temperature DOUBLE,
  humidity DOUBLE,
  timestamp TIMESTAMPTZ
);

CREATE TABLE RoomData (
  room_id UUID PRIMARY KEY,
  room_name VARCHAR,
  room_location VARCHAR
);

CREATE TABLE AirPurifier (
  purifier_id UUID PRIMARY KEY,
  purifier_name VARCHAR,
  room_id UUID,
  FOREIGN KEY (room_id) REFERENCES RoomData(room_id)
);

CREATE TABLE UserSettings (
  user_id UUID PRIMARY KEY,
  preferred_temperature DOUBLE,
  preferred_humidity DOUBLE
);
