tables.table_schema:
CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(50),
    usage_count INT
);

CREATE TABLE historical_data (
    device_id INT PRIMARY KEY,
    usage_data TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE device_information (
    device_id INT PRIMARY KEY,
    device_name TEXT,
    manufacturer TEXT,
    location TEXT
);

CREATE TABLE usage_patterns (
    pattern_id INT PRIMARY KEY,
    device_id INT REFERENCES historical_data(device_id),
    pattern_description TEXT,
    pattern_type TEXT
);

CREATE TABLE DeviceActivity (
    id SERIAL PRIMARY KEY,
    device_id INT,
    activity_type VARCHAR(50),
    activity_date DATE
);
