tables.table_schema:
CREATE TABLE sensor_data (
id INT,
sensor_type VARCHAR(255),
sensor_value VARCHAR(255),
timestamp TIMESTAMP
);

CREATE TABLE motion_detection (
    id INT PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    motion_detected BOOLEAN
);

CREATE TABLE MotionDetection (
  id INT PRIMARY KEY,
  device_id INT,
  timestamp TIMESTAMP,
  motion_detected BOOLEAN
);

CREATE TABLE DoorStatus (
  id INT PRIMARY KEY,
  device_id INT,
  timestamp TIMESTAMP,
  door_status VARCHAR
);

CREATE TABLE WindowStatus (
  id INT PRIMARY KEY,
  device_id INT,
  timestamp TIMESTAMP,
  window_status VARCHAR
);
