tables.table_schema:
CREATE TABLE security_footage (
    footage_id UUID PRIMARY KEY,
    camera_id UUID,
    timestamp TIMESTAMP,
    footage_data BYTEA
);

CREATE TABLE footage (
    camera_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    video_url TEXT
);

CREATE TABLE camera (
    camera_id UUID PRIMARY KEY,
    location TEXT,
    model TEXT,
    resolution TEXT
);

CREATE TABLE security_cameras (
    camera_id INT PRIMARY KEY,
    location VARCHAR(255),
    resolution VARCHAR(50),
    status VARCHAR(20)
);

CREATE TABLE playback_history (
    playback_id INT PRIMARY KEY,
    footage_id INT REFERENCES footage_archive(footage_id),
    playback_timestamp TIMESTAMP,
    playback_duration INT
);

CREATE TABLE Camera (
  camera_id INT PRIMARY KEY,
  camera_location VARCHAR(255)
);

CREATE TABLE cameras (
  camera_id INT PRIMARY KEY,
  location TEXT,
  type TEXT,
  resolution TEXT
);

CREATE TABLE footage_metadata (
  metadata_id INT PRIMARY KEY,
  footage_id INT,
  event_type TEXT,
  object_detected TEXT
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  name TEXT,
  email TEXT,
  password TEXT
);
