tables.table_schema:
CREATE TABLE SecurityBreaches (
    id INT PRIMARY KEY,
    date DATE,
    location VARCHAR(255),
    breach_type VARCHAR(255)
);

CREATE TABLE FalseAlarms (
    id INT PRIMARY KEY,
    date DATE,
    location VARCHAR(255),
    alarm_type VARCHAR(255)
);

CREATE TABLE SecurityBreachEvents (
    event_id INT PRIMARY KEY,
    event_date TIMESTAMP,
    breach_type VARCHAR(50),
    description TEXT
);

CREATE TABLE FalseAlarms (
    alarm_id INT PRIMARY KEY,
    alarm_date TIMESTAMP,
    alarm_type VARCHAR(50),
    alarm_details TEXT
);

CREATE TABLE SecurityIncidentLocations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100),
    city VARCHAR(50),
    country VARCHAR(50)
);

CREATE TABLE security_breaches (
  id INT PRIMARY KEY,
  date DATE,
  location VARCHAR,
  severity INT
);

CREATE TABLE false_alarms (
  id INT PRIMARY KEY,
  date DATE,
  location VARCHAR,
  reason VARCHAR
);

CREATE TABLE breach_patterns (
  pattern_id INT PRIMARY KEY,
  pattern_name VARCHAR,
  severity_level INT
);

CREATE TABLE alarm_patterns (
  pattern_id INT PRIMARY KEY,
  pattern_name VARCHAR,
  reason VARCHAR
);

CREATE TABLE security_logs (
  log_id INT PRIMARY KEY,
  event_date DATE,
  event_type VARCHAR,
  description VARCHAR
);
