CREATE TABLE smart_home_devices(
    device_id INT PRIMARY KEY,
    device_name STRING,
    device_type STRING,
    location STRING
);

CREATE TABLE security_actions(
    action_id INT PRIMARY KEY,
    action_name STRING,
    action_type STRING
);

CREATE TABLE trigger_actions(
    trigger_id INT,
    action_id INT,
    FOREIGN KEY (trigger_id) REFERENCES trigger_devices(trigger_id),
    FOREIGN KEY (action_id) REFERENCES security_actions(action_id)
);

CREATE TABLE user_permissions(
    user_id INT PRIMARY KEY,
    user_name STRING,
    device_id INT,
    FOREIGN KEY (device_id) REFERENCES smart_home_devices(device_id)
);
