tables.table_schema:
CREATE TABLE SystemStatus (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    cpu_usage FLOAT,
    memory_usage FLOAT
);

CREATE TABLE SecurityThreats (
    id INT PRIMARY KEY,
    threat_name VARCHAR(100),
    description TEXT,
    severity VARCHAR(50)
);

CREATE TABLE ThreatAlerts (
    id INT PRIMARY KEY,
    threat_id INT,
    timestamp TIMESTAMP,
    status VARCHAR(50)
);
