  voice_command_mode VARCHAR(255),
  light_theme_enabled BOOLEAN,
  temperature_unit VARCHAR(50)
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    voice_command_enabled BOOLEAN,
    volume_level INT,
    command_history JSONB
);

CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    voice_command VARCHAR(255),
    command_description TEXT
);

CREATE TABLE RoomPreferences (
    room_id INT PRIMARY KEY,
    temperature_setting INT,
    light_setting ENUM('ON', 'OFF'),
    music_playlist VARCHAR(255)
);

CREATE TABLE user_preferences (
  user_id SERIAL PRIMARY KEY,
  voice_command VARCHAR(255),
  command_type VARCHAR(50),
  command_action VARCHAR(50)
);

CREATE TABLE rooms (
  room_id SERIAL PRIMARY KEY,
  room_name VARCHAR(100),
  temperature_sensor BOOLEAN,
  light_sensor BOOLEAN
);

CREATE TABLE commands_history (
  history_id SERIAL PRIMARY KEY,
  user_id INT,
  voice_command VARCHAR(255),
  executed_at TIMESTAMP
);

CREATE TABLE devices (
  device_id SERIAL PRIMARY KEY,
  device_name VARCHAR(100),
  device_type VARCHAR(50),
  device_status BOOLEAN
);

CREATE TABLE UserPreferences (
    user_id UUID PRIMARY KEY,
    voice_command_enabled BOOLEAN,
    volume_level INTEGER,
    light_brightness INTEGER
);

CREATE TABLE VoiceCommands (
    command_id UUID PRIMARY KEY,
    command_text TEXT
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE SmartHomeDevices (
    device_id UUID PRIMARY KEY,
    device_name TEXT,
    device_type TEXT
);

CREATE TABLE UserDevices (
    user_id UUID,
    device_id UUID,
    PRIMARY KEY (user_id, device_id)
);
