tables.table_schema:
CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_email VARCHAR(100)
);

CREATE TABLE voice_assistant (
    assistant_id INT PRIMARY KEY,
    assistant_name VARCHAR(50),
    activation_status BOOLEAN
);

CREATE TABLE voice_assistants (
    assistant_id INT PRIMARY KEY,
    assistant_name VARCHAR(50),
    is_active BOOLEAN DEFAULT TRUE,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR,
    email VARCHAR,
    phone_number VARCHAR
);

CREATE TABLE voice_assistants (
    voice_assistant_id UUID PRIMARY KEY,
    assistant_name VARCHAR,
    manufacturer VARCHAR
);

CREATE TABLE smart_home_devices (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR,
    device_type VARCHAR,
    status VARCHAR
);
