tables.table_schema:
CREATE TABLE smart_home_users (
  user_id INT PRIMARY KEY,
  user_name VARCHAR(50),
  user_location VARCHAR(50),
  subscription_status BOOLEAN
);

CREATE TABLE CameraLocation (
    camera_id UUID PRIMARY KEY,
    location VARCHAR(50),
    room VARCHAR(30),
    floor INT
);

CREATE TABLE camera_locations (
    camera_id UUID PRIMARY KEY,
    location_name VARCHAR(100),
    latitude DECIMAL,
    longitude DECIMAL
);

CREATE TABLE motion_events (
    id UUID PRIMARY KEY,
    camera_id UUID,
    timestamp TIMESTAMP,
    motion_type VARCHAR(50)
);
