tables.table_schema:
CREATE TABLE Plant (
   plant_id INT PRIMARY KEY,
   plant_name VARCHAR(255),
   soil_moisture_level FLOAT
);

CREATE TABLE WateringSchedule (
   schedule_id INT PRIMARY KEY,
   plant_id INT,
   watering_time TIMESTAMP,
   FOREIGN KEY (plant_id) REFERENCES Plant(plant_id)
);

CREATE TABLE soil_moisture_sensor_data (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(50),
    moisture_level FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE plants (
    plant_id INT PRIMARY KEY,
    plant_name VARCHAR(50),
    plant_type VARCHAR(50),
    optimal_moisture_level FLOAT
);

CREATE TABLE watering_logs (
    log_id INT PRIMARY KEY,
    schedule_id INT,
    timestamp TIMESTAMP,
    amount_watered FLOAT
);

CREATE TABLE MoistureSensorData (
    sensor_id UUID PRIMARY KEY,
    location VARCHAR(255),
    soil_moisture DECIMAL,
    reading_timestamp TIMESTAMP
);

CREATE TABLE PlantList (
    plant_id UUID PRIMARY KEY,
    plant_name VARCHAR(255),
    plant_species VARCHAR(255),
    watering_schedule_interval INTERVAL
);

CREATE TABLE WateringSchedule (
    schedule_id UUID PRIMARY KEY,
    plant_id UUID,
    watering_time TIME,
    watering_duration INTERVAL
);

CREATE TABLE SensorAlerts (
    alert_id UUID PRIMARY KEY,
    sensor_id UUID,
    alert_type VARCHAR(255),
    alert_message TEXT
);

CREATE TABLE WateringLog (
    log_id UUID PRIMARY KEY,
    schedule_id UUID,
    watering_date DATE,
    watering_time TIME
);
