tables.table_schema:
CREATE TABLE light_fixtures (
    id SERIAL PRIMARY KEY,
    fixture_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL,
    sensor_id INT NOT NULL
);

CREATE TABLE LightingSchedules (
  schedule_id INT PRIMARY KEY,
  fixture_id INT,
  start_time TIMESTAMP,
  end_time TIMESTAMP
);

CREATE TABLE EnergyCosts (
  cost_id INT PRIMARY KEY,
  energy_rate DECIMAL,
  start_date DATE,
  end_date DATE
);
