tables.table_schema:
CREATE TABLE maintenance_schedule (
    fixture_id INT PRIMARY KEY,
    last_maintenance_date DATE,
    next_maintenance_date DATE,
    alert_sent BOOL
);

CREATE TABLE replacement_alerts (
    alert_id INT PRIMARY KEY,
    fixture_id INT,
    alert_date DATE,
    alert_message TEXT
);

CREATE TABLE smart_light_fixtures (
    fixture_id INT PRIMARY KEY,
    location TEXT,
    last_maintenance_date DATE,
    next_maintenance_date DATE
);

CREATE TABLE maintenance_schedule (
    id INT PRIMARY KEY,
    fixture_id INT,
    maintenance_date DATE,
    description TEXT
);

CREATE TABLE fixture (
    fixture_id INT PRIMARY KEY,
    location VARCHAR(255),
    model VARCHAR(50),
    last_maintenance_date DATE
);

CREATE TABLE replacement_schedule (
    replacement_id INT PRIMARY KEY,
    fixture_id INT,
    replacement_date DATE,
    reason TEXT
);

CREATE TABLE fixtures (
    fixture_id INT PRIMARY KEY,
    fixture_name VARCHAR(255),
    location VARCHAR(255),
    installation_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255),
    phone_number VARCHAR(15)
);
