tables.table_schema:
CREATE TABLE WeatherData (
    id SERIAL PRIMARY KEY,
    date TIMESTAMP NOT NULL,
    temperature FLOAT NOT NULL,
    humidity FLOAT NOT NULL
);

CREATE TABLE weather_data (
    weather_id INT PRIMARY KEY,
    city_name TEXT,
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE sensor_data (
    sensor_id INT PRIMARY KEY,
    location TEXT,
    ambient_light FLOAT,
    uv_index FLOAT
);

CREATE TABLE lighting_control (
    control_id INT PRIMARY KEY,
    light_intensity FLOAT,
    automated BOOLEAN
);

CREATE TABLE WeatherData (
    id INT PRIMARY KEY,
    temperature FLOAT,
    humidity FLOAT,
    wind_speed FLOAT
);

CREATE TABLE LightSensorData (
    id INT PRIMARY KEY,
    light_intensity FLOAT,
    location VARCHAR(255)
);

CREATE TABLE AdjustedLighting (
    id INT PRIMARY KEY,
    adjustment_id INT,
    brightness_level FLOAT,
    time_adjusted TIMESTAMP
);

CREATE TABLE WeatherLightingMapping (
    id INT PRIMARY KEY,
    weather_id INT,
    light_id INT
);
