tables.table_schema:
CREATE TABLE security_camera_footage (
  camera_id INT,
  timestamp TIMESTAMP,
  footage BLOB
);

CREATE TABLE smart_lighting_automation (
  lighting_id INT,
  timestamp TIMESTAMP,
  lighting_mode VARCHAR
);

CREATE TABLE security_camera_logging (
    camera_id INTEGER,
    timestamp TIMESTAMP,
    event_type VARCHAR(50),
    PRIMARY KEY (camera_id, timestamp)
);

CREATE TABLE smart_lighting_automation (
    light_id INTEGER,
    timestamp TIMESTAMP,
    setting VARCHAR(50),
    PRIMARY KEY (light_id, timestamp)
);

CREATE TABLE surveillance_control (
    control_id INTEGER,
    scenario VARCHAR(50),
    timestamp TIMESTAMP,
    PRIMARY KEY (control_id)
);

CREATE TABLE security_camera_footage (
  camera_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  footage_url VARCHAR
);

CREATE TABLE lighting_automation (
  automation_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  lighting_action VARCHAR
);

CREATE TABLE security_camera_footage (
    footage_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    camera_id VARCHAR(100),
    footage_url TEXT
);

CREATE TABLE lighting_automation (
    automation_id UUID PRIMARY KEY,
    automation_name VARCHAR(100),
    light_control BOOLEAN,
    automation_schedule TIMESTAMPTZ
);

CREATE TABLE surveillance_logs (
    log_id UUID PRIMARY KEY,
    event_type VARCHAR(100),
    event_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE cameras (
    camera_id VARCHAR(100) PRIMARY KEY,
    camera_location VARCHAR(100),
    camera_type VARCHAR(100)
);
