tables.table_schema:
CREATE TABLE energy_consumption_report (
    report_id INT PRIMARY KEY,
    device_id INT,
    consumption_kWh DECIMAL,
    date_published DATE
);

CREATE TABLE cost_savings_report (
    report_id INT PRIMARY KEY,
    device_id INT,
    savings_amount DECIMAL,
    date_published DATE
);

CREATE TABLE smart_lights (
    light_id INT PRIMARY KEY,
    location TEXT,
    power_consumption DECIMAL,
    is_smart BOOLEAN
);
