tables.table_schema:
CREATE TABLE appliance_types (
    type_id UUID PRIMARY KEY,
    appliance_name STRING,
    category STRING
);

CREATE TABLE ApplianceConsumption (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(255),
    wattage INT,
    daily_usage DECIMAL(10, 2)
);

CREATE TABLE UsagePatterns (
    pattern_id SERIAL PRIMARY KEY,
    appliance_id INT,
    pattern VARCHAR(255),
    FOREIGN KEY (appliance_id) REFERENCES ApplianceConsumption(appliance_id)
);

CREATE TABLE ApplianceConsumption (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(255),
    consumption_kWh DECIMAL(10,2),
    date_consumed DATE
);

CREATE TABLE ApplianceType (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(255)
);

CREATE TABLE RoomAppliance (
    room_id INT,
    appliance_id INT,
    PRIMARY KEY (room_id, appliance_id)
);

CREATE TABLE Room (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(255)
);

CREATE TABLE User (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    email VARCHAR(255)
);
