tables.table_schema:
CREATE TABLE energy_before_upgrade (
    id SERIAL PRIMARY KEY,
    date DATE,
    energy_consumption DECIMAL
);

CREATE TABLE energy_after_upgrade (
    id SERIAL PRIMARY KEY,
    date DATE,
    energy_consumption DECIMAL
);

CREATE TABLE EnergyConsumptionBeforeUpgrade (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  consumption_amount DECIMAL
);

CREATE TABLE EnergyConsumptionAfterUpgrade (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  consumption_amount DECIMAL
);

CREATE TABLE EnergyEfficiencyImprovements (
  id INT PRIMARY KEY,
  before_upgrade_id INT,
  after_upgrade_id INT,
  improvement_percentage DECIMAL
);
