tables.table_schema:
CREATE TABLE energy_reports (
   report_id SERIAL PRIMARY KEY,
   room_id INT NOT NULL,
   energy_usage DECIMAL NOT NULL,
   report_date DATE
);

CREATE TABLE EnergyUsageReport (
  room_id INT,
  energy_usage DECIMAL,
  timestamp TIMESTAMP
);

CREATE TABLE RoomDetails (
  room_id INT PRIMARY KEY,
  room_name VARCHAR(50),
  room_size DECIMAL
);

CREATE TABLE RoomOptimization (
  room_id INT,
  optimization_recommendation TEXT
);
