    meter_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    energy_usage DECIMAL NOT NULL
);

CREATE TABLE consumption_habits (
    habit_id INT PRIMARY KEY,
    meter_id INT NOT NULL,
    habit_description TEXT
);

CREATE TABLE smart_meter_data (
  id UUID PRIMARY KEY,
  timestamp TIMESTAMP,
  energy_usage DECIMAL
);

CREATE TABLE user_profiles (
  user_id UUID PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE consumption_history (
  timestamp TIMESTAMP,
  user_id UUID,
  energy_usage DECIMAL
);

CREATE TABLE smart_meter_data (
 meter_id INT PRIMARY KEY,
 timestamp TIMESTAMPTZ,
 energy_usage FLOAT
);

CREATE TABLE household (
 household_id INT PRIMARY KEY,
 address TEXT
);

CREATE TABLE energy_provider (
 provider_id INT PRIMARY KEY,
 name TEXT
);

CREATE TABLE consumption_habits (
 id INT PRIMARY KEY,
 household_id INT REFERENCES household(household_id),
 timestamp TIMESTAMPTZ,
 habit_score INT
);

CREATE TABLE SmartMeterData (
    id UUID PRIMARY KEY,
    meter_id UUID,
    timestamp TIMESTAMP,
    energy_usage DECIMAL,
    CONSTRAINT fk_meter_id FOREIGN KEY (meter_id) REFERENCES Meters(id)
);

CREATE TABLE Meters (
    id UUID PRIMARY KEY,
    location VARCHAR,
    owner VARCHAR,
    installed_date DATE
);

CREATE TABLE EnergyConsumption (
    id UUID PRIMARY KEY,
    meter_id UUID,
    date DATE,
    energy_consumed DECIMAL
);

CREATE TABLE ConsumptionHabits (
    id UUID PRIMARY KEY,
    user_id UUID,
    habit_description TEXT
);

CREATE TABLE Users (
    id UUID PRIMARY KEY,
    username VARCHAR,
    email VARCHAR,
    role VARCHAR
);
