tables.table_schema:
CREATE TABLE BillingCalculations (
    customer_id INT,
    energy_consumed FLOAT,
    tariff_rate FLOAT
);

CREATE TABLE energy_consumption (
    consumer_id INT PRIMARY KEY,
    consumption_amount DECIMAL,
    consumption_date DATE
);

CREATE TABLE tariff_rates (
    tariff_id INT PRIMARY KEY,
    energy_rate DECIMAL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE EnergyConsumption (
  id INT PRIMARY KEY,
  customer_id INT,
  energy_usage FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE TariffRates (
  id INT PRIMARY KEY,
  tariff_name VARCHAR(255),
  tariff_rate FLOAT
);

CREATE TABLE EnergyConsumption (
    id UUID PRIMARY KEY,
    user_id UUID,
    energy_consumed DECIMAL,
    energy_cost DECIMAL
);

CREATE TABLE TariffRates (
    id UUID PRIMARY KEY,
    tariff_name VARCHAR,
    energy_type VARCHAR,
    rate DECIMAL
);

CREATE TABLE Users (
    id UUID PRIMARY KEY,
    username VARCHAR,
    address VARCHAR,
    email VARCHAR
);

CREATE TABLE BillingHistory (
    id UUID PRIMARY KEY,
    user_id UUID,
    billing_date DATE,
    total_cost DECIMAL
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name TEXT,
  address TEXT
);

CREATE TABLE energy_consumption (
  consumption_id INT PRIMARY KEY,
  customer_id INT,
  consumption_date DATE,
  consumption_value DECIMAL
);

CREATE TABLE tariff_rates (
  rate_id INT PRIMARY KEY,
  rate_code TEXT,
  rate_value DECIMAL
);

CREATE TABLE payments (
  payment_id INT PRIMARY KEY,
  customer_id INT,
  payment_date DATE,
  amount DECIMAL
);
