tables.table_schema:
CREATE TABLE appliance_usage (
    appliance_id INT,
    usage_category VARCHAR,
    usage_hours INT,
    last_maintenance_date DATE
);

CREATE TABLE maintenance_log (
    log_id INT,
    appliance_id INT,
    maintenance_description TEXT,
    maintenance_date DATE
);

CREATE TABLE appliance_usage (
    id SERIAL PRIMARY KEY,
    appliance_name VARCHAR(255) NOT NULL,
    usage_hours INT NOT NULL,
    last_maintenance_date DATE
);

CREATE TABLE device_lifespans (
    appliance_id INT NOT NULL,
    lifespan_years INT NOT NULL,
    predicted_lifetime_years INT NOT NULL,
    PRIMARY KEY (appliance_id)
);

CREATE TABLE appliance_data (
   appliance_id INT PRIMARY KEY,
   appliance_name VARCHAR(50) NOT NULL,
   usage_hours INT,
   last_maintenance_date DATE
);

CREATE TABLE appliance_maintenance (
   maintenance_id INT PRIMARY KEY,
   appliance_id INT REFERENCES appliance_data(appliance_id),
   maintenance_type VARCHAR(50),
   maintenance_date DATE
);

CREATE TABLE appliance_sensors (
   sensor_id INT PRIMARY KEY,
   appliance_id INT REFERENCES appliance_data(appliance_id),
   sensor_type VARCHAR(50),
   sensor_value FLOAT
);

CREATE TABLE appliance_predictions (
   prediction_id INT PRIMARY KEY,
   appliance_id INT REFERENCES appliance_data(appliance_id),
   prediction_date DATE,
   predicted_issue VARCHAR(100)
);

CREATE TABLE appliance_usage (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(255) NOT NULL,
    usage_hours INT NOT NULL,
    last_maintenance_date DATE
);

CREATE TABLE appliance_models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL,
    brand VARCHAR(100) NOT NULL,
    warranty_period INT
);

CREATE TABLE appliance_types (
    type_id INT PRIMARY KEY,
    appliance_type VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE user_feedback (
    feedback_id INT PRIMARY KEY,
    appliance_id INT NOT NULL,
    rating INT,
    feedback_text TEXT
);
