tables.table_schema:
CREATE TABLE user_preferences (
  id INT PRIMARY KEY,
  user_id INT NOT NULL,
  temperature_setting FLOAT NOT NULL
);

CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    temperature_setting INT
);

CREATE TABLE TemperatureSettings (
  user_id INT PRIMARY KEY,
  preferred_temperature FLOAT
);

CREATE TABLE UserPreferences (
  user_id INT PRIMARY KEY,
  time_zone VARCHAR,
  preferred_temperature FLOAT,
  preferred_location VARCHAR
);

CREATE TABLE UserDevices (
  device_id INT PRIMARY KEY,
  user_id INT,
  device_name VARCHAR,
  device_type VARCHAR
);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  temperature_celsius INT,
  humidity_level INT
);

CREATE TABLE user_roles (
  role_id INT PRIMARY KEY,
  role_name VARCHAR(50)
);

CREATE TABLE user_notifications (
  notification_id INT PRIMARY KEY,
  user_id INT,
  message TEXT,
  notification_time TIMESTAMP
);

CREATE TABLE user_devices (
  device_id INT PRIMARY KEY,
  user_id INT,
  device_name VARCHAR(50),
  device_type VARCHAR(50)
);

CREATE TABLE temperature_settings (
  user_id INT PRIMARY KEY,
  temperature_value FLOAT
);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  preferred_location VARCHAR(255),
  is_smart_mode BOOLEAN
);

CREATE TABLE ac_settings (
  ac_id INT PRIMARY KEY,
  power_mode VARCHAR(50),
  temperature_unit VARCHAR(10)
);

CREATE TABLE user_ac_rel (
  user_id INT,
  ac_id INT,
  FOREIGN KEY (user_id) REFERENCES user_preferences(user_id),
  FOREIGN KEY (ac_id) REFERENCES ac_settings(ac_id)
);

CREATE TABLE user_history (
  user_id INT,
  event_time TIMESTAMP,
  event_description VARCHAR(255)
);
