tables.table_schema:
CREATE TABLE energy_consumption_data (
  appliance_id INT PRIMARY KEY,
  appliance_name VARCHAR(50),
  energy_consumed DECIMAL(10,2),
  timestamp TIMESTAMP
);

CREATE TABLE energy_consumption_data (
    appliance_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    energy_consumed FLOAT NOT NULL
);

CREATE TABLE appliance_details (
    appliance_id INT PRIMARY KEY,
    appliance_name VARCHAR(255) NOT NULL,
    manufacturer VARCHAR(255) NOT NULL,
    energy_rating FLOAT NOT NULL
);

CREATE TABLE EnergyConsumptionLogs (
    log_id INT PRIMARY KEY,
    appliance_id INT,
    log_timestamp TIMESTAMP,
    energy_consumed DECIMAL
);

CREATE TABLE EnergyConsumptionAnalysis (
    analysis_id INT PRIMARY KEY,
    log_id INT,
    analysis_timestamp TIMESTAMP,
    energy_saving_opportunity TEXT
);

CREATE TABLE EnergyConsumptionOptimization (
    optimization_id INT PRIMARY KEY,
    analysis_id INT,
    optimization_timestamp TIMESTAMP,
    optimization_strategy TEXT
);
