tables.table_schema:
CREATE TABLE user_settings (
    user_id UUID PRIMARY KEY,
    temperature_setting FLOAT,
    humidity_setting FLOAT,
    energy_saving_mode BOOLEAN
);

CREATE TABLE recommendation (
    recommendation_id SERIAL PRIMARY KEY,
    user_id UUID,
    recommended_temperature FLOAT,
    recommended_humidity FLOAT
);

CREATE TABLE refrigerator_usage (
    user_id UUID,
    temperature INT,
    humidity INT,
    timestamp TIMESTAMP
);

CREATE TABLE user_profile (
    user_id UUID,
    name VARCHAR(100),
    email VARCHAR(100),
    address VARCHAR(255)
);

CREATE TABLE fridge_inventory (
    fridge_id UUID,
    model VARCHAR(50),
    manufacturer VARCHAR(50),
    purchase_date DATE
);

CREATE TABLE refrigerator_usage (
  user_id UUID,
  temperature FLOAT,
  humidity FLOAT,
  timestamp TIMESTAMP
);

CREATE TABLE recommended_settings (
  user_id UUID,
  recommended_temperature FLOAT,
  recommended_humidity FLOAT
);

CREATE TABLE notification_logs (
  user_id UUID,
  message VARCHAR,
  timestamp TIMESTAMP
);

CREATE TABLE refrigerator_settings (
  user_id UUID,
  current_temperature FLOAT,
  current_humidity FLOAT,
  power_saving_mode BOOLEAN
);
