tables.table_schema:
CREATE TABLE cooking_instructions (
  instruction_id INT PRIMARY KEY,
  recipe_id INT,
  step_number INT,
  instructions TEXT
);

CREATE TABLE recipe (
  recipe_id SERIAL PRIMARY KEY,
  recipe_name VARCHAR(255),
  ingredients TEXT[],
  cooking_instructions TEXT
);

CREATE TABLE cooking_times (
  cooking_time_id SERIAL PRIMARY KEY,
  recipe_id INT,
  cooking_time INTERVAL,
  FOREIGN KEY (recipe_id) REFERENCES recipe(recipe_id)
);

CREATE TABLE oven (
  oven_id SERIAL PRIMARY KEY,
  oven_name VARCHAR(255),
  oven_description TEXT,
  cooking_capacity INT
);
