tables.table_schema:
CREATE TABLE washing_machine_data (
  id INT PRIMARY KEY,
  machine_id INT,
  water_usage DECIMAL,
  timestamp TIMESTAMP
);

CREATE TABLE machine_locations (
  id INT PRIMARY KEY,
  machine_id INT,
  location VARCHAR
);

CREATE TABLE washing_machines (
    id INT PRIMARY KEY,
    serial_number VARCHAR(255) NOT NULL,
    manufacturer VARCHAR(255) NOT NULL,
    model VARCHAR(255) NOT NULL
);

CREATE TABLE water_usage_data (
    id INT PRIMARY KEY,
    machine_id INT,
    usage_date DATE,
    total_water_used FLOAT
);

CREATE TABLE WashingMachine ( 
    id INT PRIMARY KEY, 
    model_number INT, 
    manufacturer VARCHAR(255), 
    installation_date DATE 
);

CREATE TABLE WaterUsage ( 
    id INT PRIMARY KEY, 
    machine_id INT, 
    usage_date DATE, 
    gallons_used FLOAT 
);

CREATE TABLE User ( 
    id INT PRIMARY KEY, 
    username VARCHAR(255), 
    email VARCHAR(255), 
    location VARCHAR(255) 
);

CREATE TABLE WaterConservation ( 
    id INT PRIMARY KEY, 
    conservation_measure VARCHAR(255), 
    saving_estimate FLOAT, 
    implementation_date DATE 
);

CREATE TABLE MachineStatus ( 
    id INT PRIMARY KEY, 
    machine_id INT, 
    status VARCHAR(255), 
    update_timestamp TIMESTAMP 
);
