tables.table_schema:
CREATE TABLE lighting_control (
    light_id INT PRIMARY KEY,
    light_name VARCHAR(50),
    brightness INT,
    color VARCHAR(20)
);

CREATE TABLE temperature_control (
    sensor_id INT PRIMARY KEY,
    room_name VARCHAR(50),
    temperature FLOAT,
    humidity FLOAT
);

CREATE TABLE lighting_control (
    id INT PRIMARY KEY,
    room_id INT,
    light_status ENUM('on', 'off')
);

CREATE TABLE temperature_control (
    id INT PRIMARY KEY,
    room_id INT,
    temperature DECIMAL(5,2)
);

CREATE TABLE room_info (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(50),
    room_type ENUM('living_room', 'bedroom', 'kitchen')
);

CREATE TABLE lighting_control (
    lighting_id INT PRIMARY KEY,
    room_id INT,
    light_intensity INT,
    light_status TEXT
);

CREATE TABLE temperature_control (
    temperature_id INT PRIMARY KEY,
    room_id INT,
    temperature_value DECIMAL,
    temperature_unit TEXT
);

CREATE TABLE room_info (
    room_id INT PRIMARY KEY,
    room_name TEXT,
    room_location TEXT,
    room_type TEXT
);

CREATE TABLE schedule_tasks (
    task_id INT PRIMARY KEY,
    room_id INT,
    task_description TEXT,
    task_time TIME
);

CREATE TABLE lights (
    light_id INT PRIMARY KEY,
    room_id INT,
    light_name VARCHAR(50),
    is_on BOOLEAN
);

CREATE TABLE temperatures (
    temperature_id INT PRIMARY KEY,
    room_id INT,
    temperature_value DECIMAL,
    datetime TIMESTAMP
);

CREATE TABLE rooms (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(50),
    floor_number INT,
    house_id INT
);

CREATE TABLE houses (
    house_id INT PRIMARY KEY,
    house_name VARCHAR(50),
    address VARCHAR(100),
    owner_id INT
);

CREATE TABLE owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(50),
    email VARCHAR(50)
);
