tables.table_schema:
CREATE TABLE network_traffic_monitor (
    ip_address VARCHAR(15) NOT NULL,
    mac_address VARCHAR(17) NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    data_usage INT NOT NULL
);

CREATE TABLE security_threats (
    id INT PRIMARY KEY,
    threat_name VARCHAR(255),
    threat_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE NetworkDevices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  ip_address VARCHAR(15),
  mac_address VARCHAR(17)
);

CREATE TABLE ThreatSignatures (
  threat_id INT PRIMARY KEY,
  threat_name VARCHAR(50),
  description TEXT
);

CREATE TABLE network_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(50),
    ip_address VARCHAR(15)
);

CREATE TABLE network_traffic_logs (
    log_id INT PRIMARY KEY,
    device_id INT,
    timestamp TIMESTAMP,
    data_usage INT
);

CREATE TABLE security_threats (
    threat_id INT PRIMARY KEY,
    threat_type VARCHAR(50),
    description TEXT,
    severity VARCHAR(10)
);

CREATE TABLE threat_alerts (
    alert_id INT PRIMARY KEY,
    threat_id INT,
    device_id INT,
    alert_timestamp TIMESTAMP
);

CREATE TABLE network_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(20),
    ip_address VARCHAR(15)
);

CREATE TABLE security_rules (
    rule_id SERIAL PRIMARY KEY,
    source_ip VARCHAR(15),
    destination_ip VARCHAR(15),
    action VARCHAR(10)
);
