tables.table_schema:
CREATE TABLE content_assets (
    id INT PRIMARY KEY,
    title TEXT,
    description TEXT,
    tags TEXT
);

CREATE TABLE asset_tags (
    tag_id INT PRIMARY KEY,
    tag_name TEXT
);

CREATE TABLE asset_metadata (
    id UUID PRIMARY KEY,
    title TEXT,
    description TEXT,
    tags TEXT[]
);

CREATE TABLE asset_tags (
    tag_id UUID PRIMARY KEY,
    tag_name TEXT UNIQUE
);

CREATE TABLE asset_categories (
    category_id UUID PRIMARY KEY,
    category_name TEXT UNIQUE
);

CREATE TABLE ContentAssets (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    description TEXT,
    tags VARCHAR(255)
);

CREATE TABLE Titles (
    id INT PRIMARY KEY,
    content_id INT,
    title VARCHAR(255),
    FOREIGN KEY (content_id) REFERENCES ContentAssets(id)
);

CREATE TABLE Descriptions (
    id INT PRIMARY KEY,
    content_id INT,
    description TEXT,
    FOREIGN KEY (content_id) REFERENCES ContentAssets(id)
);

CREATE TABLE Tags (
    id INT PRIMARY KEY,
    content_id INT,
    tag VARCHAR(50),
    FOREIGN KEY (content_id) REFERENCES ContentAssets(id)
);

CREATE TABLE ContentAssets (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    description TEXT,
    tags VARCHAR(255)
);

CREATE TABLE Tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50)
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255)
);

CREATE TABLE ContentTags (
    content_id INT,
    tag_id INT,
    FOREIGN KEY (content_id) REFERENCES ContentAssets(id),
    FOREIGN KEY (tag_id) REFERENCES Tags(tag_id)
);

CREATE TABLE ContentAuthors (
    content_id INT,
    user_id INT,
    FOREIGN KEY (content_id) REFERENCES ContentAssets(id),
    FOREIGN KEY (user_id) REFERENCES Users(user_id)
);
