tables.table_schema:
CREATE TABLE translations (
  translation_id SERIAL PRIMARY KEY,
  content_id INT,
  language_code VARCHAR(10),
  translated_text TEXT
);

CREATE TABLE images (
  image_id SERIAL PRIMARY KEY,
  content_id INT,
  image_url TEXT,
  caption TEXT
);

CREATE TABLE cultural_adaptations (
  adaptation_id SERIAL PRIMARY KEY,
  content_id INT,
  cultural_context TEXT,
  adaptation_text TEXT
);

CREATE TABLE TranslatedText (
  id INT PRIMARY KEY,
  language_code VARCHAR(2),
  text_content TEXT
);

CREATE TABLE TranslatedImage (
  id INT PRIMARY KEY,
  language_code VARCHAR(2),
  image_data BYTEA
);

CREATE TABLE CulturalAdaptions (
  id INT PRIMARY KEY,
  language_code VARCHAR(2),
  cultural_info JSON
);

CREATE TABLE LocalizedContent (
  id INT PRIMARY KEY,
  language_code VARCHAR(2),
  content_type VARCHAR(50),
  content_data TEXT
);

CREATE TABLE translated_text (
    text_id INT PRIMARY KEY,
    language_code VARCHAR(10),
    text_content TEXT
);

CREATE TABLE translated_image (
    image_id INT PRIMARY KEY,
    language_code VARCHAR(10),
    image_url VARCHAR(255)
);

CREATE TABLE cultural_adaption (
    adaption_id INT PRIMARY KEY,
    language_code VARCHAR(10),
    cultural_details TEXT
);

CREATE TABLE content_metadata (
    content_id INT PRIMARY KEY,
    content_type VARCHAR(50),
    create_date TIMESTAMP
);

CREATE TABLE language_mapping (
    language_id INT PRIMARY KEY,
    language_code VARCHAR(10),
    language_name VARCHAR(50)
);
