tables.table_schema:
CREATE TABLE content_distribution_channels (
  channel_id INT PRIMARY KEY,
  channel_name TEXT,
  platform TEXT
);

CREATE TABLE ContentChannels (
    channel_id INT PRIMARY KEY,
    channel_name VARCHAR(50) NOT NULL,
    platform VARCHAR(20) NOT NULL
);

CREATE TABLE Websites (
    website_id INT PRIMARY KEY,
    website_name VARCHAR(50) NOT NULL,
    website_url VARCHAR(255) NOT NULL
);

CREATE TABLE SocialMedia (
    social_media_id INT PRIMARY KEY,
    social_media_name VARCHAR(50) NOT NULL,
    followers_count INT
);

CREATE TABLE MobileApps (
    app_id INT PRIMARY KEY,
    app_name VARCHAR(50) NOT NULL,
    downloads_count INT
);

CREATE TABLE website_content (
    content_id SERIAL PRIMARY KEY,
    content_title VARCHAR(255),
    content_type VARCHAR(50),
    publish_date DATE
);

CREATE TABLE social_media_content (
    post_id SERIAL PRIMARY KEY,
    post_text TEXT,
    post_platform VARCHAR(50),
    post_date DATE
);

CREATE TABLE app_content (
    app_id SERIAL PRIMARY KEY,
    app_name VARCHAR(100),
    app_category VARCHAR(50),
    app_platform VARCHAR(50)
);
