tables.table_schema:
CREATE TABLE email_stats (
    email_id SERIAL PRIMARY KEY,
    sent_date DATE NOT NULL,
    open_rate FLOAT NOT NULL,
    click_through_rate FLOAT NOT NULL
);

CREATE TABLE email_open_rates (
    email_id INT,
    open_timestamp TIMESTAMP,
    open_duration INT,
    user_id INT
);

CREATE TABLE email_campaigns (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE email_lists (
  list_id INT PRIMARY KEY,
  list_name VARCHAR(255),
  number_of_recipients INT,
  create_date DATE
);

CREATE TABLE email_clicks (
  click_id INT PRIMARY KEY,
  recipient_id INT,
  click_timestamp TIMESTAMP,
  FOREIGN KEY (recipient_id) REFERENCES email_recipients(recipient_id)
);

CREATE TABLE open_rates (
    email_id INT PRIMARY KEY,
    open_date TIMESTAMP,
    open_time TIME,
    open_status VARCHAR(10)
);

CREATE TABLE click_through_rates (
    email_id INT PRIMARY KEY,
    click_date TIMESTAMP,
    click_time TIME,
    click_status VARCHAR(10)
);
