tables.table_schema:
CREATE TABLE email_delivery_status (
    id SERIAL PRIMARY KEY,
    email_address VARCHAR(255) NOT NULL,
    delivery_status VARCHAR(50) NOT NULL,
    delivery_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE delivery_status (
    id INT PRIMARY KEY,
    email_address VARCHAR(255),
    status VARCHAR(50),
    delivery_date TIMESTAMP
);

CREATE TABLE delivery_issues (
    id SERIAL PRIMARY KEY,
    email_address VARCHAR(255),
    issue_description TEXT,
    reported_date TIMESTAMP
);

CREATE TABLE EmailStatus (
    id INT PRIMARY KEY,
    email_address VARCHAR(255) NOT NULL,
    delivery_status VARCHAR(50) NOT NULL,
    delivery_time TIMESTAMP
);

CREATE TABLE BouncedEmails (
    id INT PRIMARY KEY,
    email_id INT REFERENCES EmailStatus(id),
    bounced_reason VARCHAR(255) NOT NULL,
    bounce_time TIMESTAMP
);

CREATE TABLE OpenedEmails (
    id INT PRIMARY KEY,
    email_id INT REFERENCES EmailStatus(id),
    open_time TIMESTAMP
);

CREATE TABLE ClickedLinks (
    id INT PRIMARY KEY,
    email_id INT REFERENCES EmailStatus(id),
    link_url VARCHAR(255) NOT NULL,
    click_time TIMESTAMP
);
