tables.table_schema:
CREATE TABLE email_subscriptions (
  email_id INT PRIMARY KEY,
  email_address TEXT NOT NULL,
  segment TEXT,
  engagement_level INT
);

CREATE TABLE email_subscriptions (
    subscription_id UUID PRIMARY KEY,
    email_address VARCHAR(255) NOT NULL,
    subscription_date TIMESTAMP,
    is_active BOOLEAN
);

CREATE TABLE customer_segments (
    segment_id SERIAL PRIMARY KEY,
    customer_id UUID,
    segment_name VARCHAR(50) NOT NULL
);

CREATE TABLE SubscriptionList (
    subscription_id INT PRIMARY KEY,
    email_address VARCHAR(255),
    subscription_date DATE,
    engagement_level ENUM('low', 'medium', 'high')
);

CREATE TABLE CustomerSegment (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(255),
    segment_description TEXT
);

CREATE TABLE CustomerEngagement (
    engagement_id INT PRIMARY KEY,
    customer_id INT,
    engagement_date DATE,
    engagement_type ENUM('open', 'click', 'purchase')
);

CREATE TABLE Email_Subscribers (
    subscriber_id INT PRIMARY KEY,
    email_address VARCHAR(255) UNIQUE,
    subscription_date TIMESTAMP,
    engagement_level VARCHAR(50)
);

CREATE TABLE Engagement_Segments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(100) UNIQUE,
    description TEXT
);

CREATE TABLE Subscriber_Attributes (
    attribute_id INT PRIMARY KEY,
    subscriber_id INT,
    attribute_name VARCHAR(100),
    attribute_value TEXT
);

CREATE TABLE Segment_Subscriptions (
    segment_id INT,
    subscriber_id INT,
    subscription_date TIMESTAMP,
    PRIMARY KEY (segment_id, subscriber_id)
);

CREATE TABLE SubscriptionLists (
    list_id INT PRIMARY KEY,
    list_name VARCHAR(255) NOT NULL,
    created_date DATE
);

CREATE TABLE EngagementLevels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(50) NOT NULL
);
