tables.table_schema:
CREATE TABLE customer_behavior_triggers (
    trigger_id INT PRIMARY KEY,
    customer_id INT,
    trigger_name VARCHAR(50),
    trigger_condition VARCHAR(100)
);

CREATE TABLE email_scheduling (
    email_id INT PRIMARY KEY,
    customer_id INT,
    email_subject VARCHAR(255),
    email_body TEXT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE email_triggers (
    trigger_id INT PRIMARY KEY,
    trigger_name VARCHAR(50),
    behavior_id INT,
    FOREIGN KEY (behavior_id) REFERENCES behaviors(behavior_id)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    email_address VARCHAR(255) NOT NULL,
    name VARCHAR(50) NOT NULL,
    behavior_trigger VARCHAR(100) NOT NULL
);

CREATE TABLE emails (
    email_id INT PRIMARY KEY,
    subject VARCHAR(255) NOT NULL,
    body TEXT NOT NULL,
    scheduled_time TIMESTAMP NOT NULL
);

CREATE TABLE triggers (
    trigger_id INT PRIMARY KEY,
    trigger_name VARCHAR(100) NOT NULL,
    description TEXT NOT NULL
);

CREATE TABLE email_logs (
    log_id INT PRIMARY KEY,
    email_id INT NOT NULL,
    customer_id INT NOT NULL,
    log_time TIMESTAMP NOT NULL
);
