tables.table_schema:
CREATE TABLE duplicate_content_issues (
    id INT PRIMARY KEY,
    page_url VARCHAR(255),
    duplicate_content TEXT
);

CREATE TABLE resolved_duplicate_content (
    id INT PRIMARY KEY,
    original_url VARCHAR(255),
    resolved_content TEXT
);

CREATE TABLE WebsitePages (
    page_id INT PRIMARY KEY,
    page_title VARCHAR(255) NOT NULL,
    content TEXT NOT NULL
);

CREATE TABLE DuplicatePages (
    duplicate_id INT PRIMARY KEY,
    page_id INT NOT NULL,
    duplicate_content TEXT NOT NULL,
    FOREIGN KEY (page_id) REFERENCES WebsitePages(page_id)
);

CREATE TABLE website_pages (
    page_id INT PRIMARY KEY,
    page_url VARCHAR(255) NOT NULL,
    content TEXT NOT NULL
);

CREATE TABLE duplicate_content (
    duplicate_id INT PRIMARY KEY,
    parent_page_id INT NOT NULL,
    duplicate_page_id INT NOT NULL
);

CREATE TABLE content_issues (
    issue_id INT PRIMARY KEY,
    page_id INT NOT NULL,
    issue_description TEXT NOT NULL
);

CREATE TABLE resolved_issues (
    resolved_id INT PRIMARY KEY,
    issue_id INT NOT NULL,
    resolution TEXT NOT NULL
);

CREATE TABLE Duplicate_Content_Report (
    report_id INT PRIMARY KEY,
    report_name VARCHAR(255),
    report_description TEXT
);

CREATE TABLE Duplicate_Content_Fix (
    fix_id INT PRIMARY KEY,
    fix_name VARCHAR(255),
    fix_description TEXT
);
