tables.table_schema:
CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100),
    country VARCHAR(50)
);

CREATE TABLE campaign (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE client (
    client_id INT PRIMARY KEY,
    client_name VARCHAR(255),
    industry VARCHAR(255)
);

CREATE TABLE ad (
    ad_id INT PRIMARY KEY,
    ad_name VARCHAR(255),
    ad_type VARCHAR(50),
    client_id INT
);
