tables.table_schema:
CREATE TABLE InfluencerContentMetadata (
    influencer_id INT PRIMARY KEY,
    content_id INT,
    performance_data JSON
);

CREATE TABLE ContentPerformanceData (
    content_id INT PRIMARY KEY,
    views INT,
    likes INT,
    shares INT
);

CREATE TABLE Influencers (
    id UUID PRIMARY KEY,
    name STRING,
    followers INT,
    engagement_rate FLOAT
);

CREATE TABLE Influencers (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255) NOT NULL,
    followers_count INT,
    avg_likes_per_post DECIMAL
);

CREATE TABLE PerformanceData (
    performance_id INT PRIMARY KEY,
    post_id INT,
    impression_count INT,
    engagement_rate DECIMAL
);

CREATE TABLE Optimization (
    optimization_id INT PRIMARY KEY,
    post_id INT,
    optimized_text TEXT,
    optimized_image_url VARCHAR(255)
);

CREATE TABLE Influencer (
    influencer_id INT PRIMARY KEY,
    influencer_name VARCHAR(255),
    followers_count INT,
    content_count INT
);

CREATE TABLE Optimization (
    optimization_id INT PRIMARY KEY,
    content_id INT,
    strategy VARCHAR(100),
    optimization_score FLOAT
);

CREATE TABLE Recommendation (
    recommendation_id INT PRIMARY KEY,
    content_id INT,
    recommended_content_id INT,
    recommendation_score FLOAT
);
