tables.table_schema:
CREATE TABLE Advertisers (
    advertiser_id INT PRIMARY KEY,
    advertiser_name VARCHAR(255),
    segment_criteria VARCHAR(255)
);

CREATE TABLE advertisers (
    id UUID PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    created_at TIMESTAMP
);

CREATE TABLE advertisers (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  industry VARCHAR(100),
  segment_id INT
);

CREATE TABLE Advertisers (
    advertiser_id INT PRIMARY KEY,
    advertiser_name VARCHAR(255) NOT NULL,
    contact_email VARCHAR(255) NOT NULL
);

CREATE TABLE AudienceSegments (
    segment_id INT PRIMARY KEY,
    segment_name VARCHAR(255) NOT NULL,
    audience_count INT NOT NULL
);

CREATE TABLE AdContent (
    content_id INT PRIMARY KEY,
    content_title VARCHAR(255) NOT NULL,
    content_type VARCHAR(50) NOT NULL
);

CREATE TABLE advertisers (
    advertiser_id UUID PRIMARY KEY,
    advertiser_name VARCHAR(255) NOT NULL,
    target_audience VARCHAR(255) NOT NULL
);

CREATE TABLE segments (
    segment_id UUID PRIMARY KEY,
    segment_name VARCHAR(255) NOT NULL,
    audience_criteria TEXT NOT NULL
);

CREATE TABLE ads (
    ad_id UUID PRIMARY KEY,
    ad_content TEXT NOT NULL,
    ad_type VARCHAR(50) NOT NULL,
    ad_owner_uuid UUID NOT NULL
);

CREATE TABLE audience_data (
    data_id UUID PRIMARY KEY,
    user_id UUID NOT NULL,
    user_interests TEXT,
    user_location VARCHAR(255) NOT NULL
);

CREATE TABLE ad_mapping (
    ad_segment_mapping_id UUID PRIMARY KEY,
    ad_id UUID NOT NULL,
    segment_id UUID NOT NULL
);
