tables.table_schema:
CREATE TABLE Order ( 
   order_id SERIAL PRIMARY KEY, 
   customer_id INT REFERENCES Customer(customer_id), 
   order_date DATE, 
   total_amount DECIMAL(10, 2) 
);

CREATE TABLE Product ( 
   product_id SERIAL PRIMARY KEY, 
   product_name VARCHAR(100), 
   category VARCHAR(50), 
   price DECIMAL(10, 2) 
);
