tables.table_schema:
CREATE TABLE sentiments (
    id INT PRIMARY KEY,
    tweet_id INT,
    sentiment_score FLOAT,
    sentiment_category VARCHAR
);

CREATE TABLE tweets (
    tweet_id INT PRIMARY KEY,
    user_id INT,
    tweet_text TEXT,
    created_at TIMESTAMP
);

CREATE TABLE social_media_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    followers_count INT,
    following_count INT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username TEXT,
    email TEXT,
    join_date DATE
);

CREATE TABLE posts (
    post_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    post_text TEXT,
    post_date DATE
);
