tables.table_schema:
CREATE TABLE feedback_data (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE product_features (
    feature_id INT PRIMARY KEY,
    feature_name VARCHAR(255),
    description TEXT
);

CREATE TABLE customer_feedback (
  feedback_id UUID PRIMARY KEY,
  customer_id UUID,
  feedback_text TEXT,
  feedback_date DATE
);

CREATE TABLE product_features (
  feature_id UUID PRIMARY KEY,
  feature_name VARCHAR(50),
  feature_description TEXT,
  feature_status VARCHAR(10)
);

CREATE TABLE customer_satisfaction (
  satisfaction_id UUID PRIMARY KEY,
  customer_id UUID,
  satisfaction_score INT,
  satisfaction_date DATE
);

CREATE TABLE ProductFeatures (
    feature_id UUID PRIMARY KEY,
    feature_name TEXT,
    feature_description TEXT
);

CREATE TABLE CustomerSatisfaction (
    satisfaction_id UUID PRIMARY KEY,
    customer_id UUID,
    satisfaction_score INT,
    satisfaction_date TIMESTAMP
);

CREATE TABLE ProductImprovement (
    improvement_id UUID PRIMARY KEY,
    product_id UUID,
    improvement_description TEXT,
    improvement_date TIMESTAMP
);
