tables.table_schema:
CREATE TABLE ad_copy_variations (
    id INT PRIMARY KEY,
    ad_copy TEXT,
    test_results JSONB
);

CREATE TABLE ad_copy_testing_results (
  id BIGINT PRIMARY KEY,
  ad_copy_id BIGINT NOT NULL,
  variation_id BIGINT NOT NULL,
  test_result JSONB NOT NULL
);

CREATE TABLE ad_copy_variations (
  id BIGINT PRIMARY KEY,
  ad_copy_id BIGINT NOT NULL,
  variation_text TEXT NOT NULL,
  created_at TIMESTAMP NOT NULL DEFAULT now()
);
